mapper.select:
select a.id, a.staff_id, s.staff_code, s.staff_name, s.dep_id, a.month, a.back_salary, a.annual_bonus, a.safety_bonus, a.stability_bonus, family_planning_bonus, excellence_bonus, a.special_bonus, a.meal_allowance, a.traffic_allowance, a.communication_allowance, a.festival_allowance, a.other_allowance, a.birthday_card, a.cool_drink, a.condolence_goods, a.rent, a.phone_bill, a.remarks, a.created_by, a.created_date, a.last_modified_by, a.last_modified_date from hr_allowance a left join hr_staff s on a.staff_id = s.id
and (s.staff_code like concat ('%', #{keyword}, '%') or s.staff_name like concat ('%', #{keyword}, '%'))
and s.dep_id = #{depId}
and date_format(a.month, '%Y-%m') = #{month}

select @rownum:=@rownum+1 as rowNum, t.* from ( select d.name as depName, s.staff_code, s.staff_name, a.month, a.back_salary, a.annual_bonus, a.safety_bonus, a.stability_bonus, family_planning_bonus, excellence_bonus, a.special_bonus, a.meal_allowance, a.traffic_allowance, a.communication_allowance, a.festival_allowance, a.other_allowance, a.birthday_card, a.cool_drink, a.condolence_goods, a.rent, a.phone_bill, a.remarks, a.created_by, a.created_date, a.last_modified_by, a.last_modified_date from hr_allowance a left join hr_staff s on a.staff_id = s.id left join sys_department d on d.id = s.dep_id
and (s.staff_code like concat ('%', #{keyword}, '%') or s.staff_name like concat ('%', #{keyword}, '%'))
and s.dep_id = #{depId}
and date_format(a.month, '%Y-%m') = #{month}
) t, (SELECT @rownum:=0) r
