mapper.select:
select p.id, p.staff_id, p.year, p.quarter, p.appraise, p.remarks, s.staff_code, s.staff_name, s.dep_id, p.created_by, p.created_date, p.last_modified_by, p.last_modified_date from hr_performance p left join hr_staff s on p.staff_id = s.id
and (s.staff_code like concat ('%', #{keyword}, '%') or s.staff_name like concat ('%', #{keyword}, '%'))
and s.dep_id = #{depId}
