mapper.select:
select sc.id, sc.staff_id, s.dep_id, s.staff_name, s.staff_code, sc.pre_basic_salary, sc.post_basic_salary, sc.pre_post_salary, sc.post_post_salary, sc.pre_accumulation_fund, sc.post_accumulation_fund, sc.pre_have_one_child_allowance, sc.post_have_one_child_allowance, sc.pre_safety_grade, sc.post_safety_grade, sc.pre_hot_weather_grade, sc.post_hot_weather_grade, sc.effective_date, sc.change_date, sc.executed, sc.remarks, sc.created_by, sc.created_date, sc.last_modified_by, sc.last_modified_date from hr_salary_change sc left join hr_staff s on sc.staff_id = s.id
and sc.staff_id = #{staffId}
and (s.staff_name like concat ('%', #{keyword}, '%') or s.staff_code like concat ('%', #{keyword}, '%'))
and s.dep_id = #{depId}

select @rownum:=@rownum+1 as rowNum, t.* from ( select d.name as depName, s.staff_code, s.staff_name, sc.pre_basic_salary, sc.post_basic_salary, sc.pre_post_salary, sc.post_post_salary, sc.pre_accumulation_fund, sc.post_accumulation_fund, sc.pre_have_one_child_allowance, sc.post_have_one_child_allowance, sc.pre_safety_grade, sc.post_safety_grade, sc.pre_hot_weather_grade, sc.post_hot_weather_grade, sc.effective_date, sc.change_date, sc.executed, sc.remarks from hr_salary_change sc left join hr_staff s on sc.staff_id = s.id left join sys_department d on s.dep_id = d.id
and (s.staff_code like concat ('%', #{keyword}, '%') or s.staff_name like concat ('%', #{keyword}, '%'))
and s.dep_id = #{depId}
) t, (SELECT @rownum:=0) r
