mapper.select:
select sa.id, sa.staff_id, st.staff_code, st.staff_name, st.dep_id, sa.month, sa.type, sa.appraise, sa.basic_salary, sa.post_salary, sa.merit_salary, sa.sick_salary, sa.back_salary, sa.annual_bonus, sa.safety_bonus, sa.stability_bonus, sa.family_planning_bonus, sa.excellence_bonus, sa.special_bonus, sa.one_child_allowance, sa.hot_weather_allowance, sa.full_attendance_allowance, sa.night_shift_allowance, sa.on_duty_allowance, sa.meal_allowance, sa.traffic_allowance, sa.festival_allowance, sa.safety_allowance, sa.communication_allowance, sa.other_allowance, sa.sick_leave_deduct, sa.entry_exit_deduct, sa.full_attendance_deduct, sa.merit_deduct, sa.birthday_card, sa.cool_drink, sa.condolence_goods, sa.accumulation_fund, sa.endowment_insurance, sa.medical_insurance, sa.union_fees, sa.rent, sa.phone_bill, sa.individual_income_tax, sa.other_aft_tax_deduct, sa.should_salary, sa.actual_salary, sa.remarks, sa.created_by, u.name as createdByName, sa.created_date, sa.last_modified_by, sa.last_modified_date from hr_salary sa left join hr_staff st on sa.staff_id = st.id left join sys_user u on u.id = sa.created_by
and (st.staff_code like concat ('%', #{keyword}, '%') or st.staff_name like concat ('%', #{keyword}, '%'))
and s.dep_id = #{depId}

select @rownum:=@rownum+1 as rowNum, t.* from ( select sa.id, sa.staff_id, st.staff_code, st.staff_name, st.dep_id, d.name as depName, sa.month, sa.type, sa.appraise, sa.basic_salary, sa.post_salary, sa.merit_salary, sa.sick_salary, sa.back_salary, sa.annual_bonus, sa.safety_bonus, sa.stability_bonus, sa.family_planning_bonus, sa.excellence_bonus, sa.special_bonus, sa.one_child_allowance, sa.hot_weather_allowance, sa.full_attendance_allowance, sa.night_shift_allowance, sa.on_duty_allowance, sa.meal_allowance, sa.traffic_allowance, sa.festival_allowance, sa.safety_allowance, sa.communication_allowance, sa.other_allowance, sa.sick_leave_deduct, sa.entry_exit_deduct, sa.full_attendance_deduct, sa.merit_deduct, sa.birthday_card, sa.cool_drink, sa.condolence_goods, sa.accumulation_fund, sa.endowment_insurance, sa.medical_insurance, sa.union_fees, sa.rent, sa.phone_bill, sa.individual_income_tax, sa.other_aft_tax_deduct, sa.should_salary, sa.actual_salary, sa.remarks, sa.created_by, u.name as createdByName, sa.created_date, sa.last_modified_by, sa.last_modified_date from hr_salary sa left join hr_staff st on sa.staff_id = st.id left join sys_department d on d.id = st.dep_id
and (st.staff_code like concat ('%', #{keyword}, '%') or st.staff_name like concat ('%', #{keyword}, '%'))
and st.dep_id = #{depId}
) t, (SELECT @rownum:=0) r
