mapper.select:
select ss.id, ss.staff_id, ss.basic_salary, ss.post_salary, s.staff_code, s.staff_name, s.dep_id, ss.created_by, ss.created_date, ss.last_modified_by, ss.last_modified_date from hr_salary_staff ss left join hr_staff s on ss.staff_id = s.id
and (s.staff_code like concat ('%', #{keyword}, '%') or s.staff_name like concat ('%', #{keyword}, '%'))
and s.dep_id = #{depId}

select @rownum:=@rownum+1 as rowNum, t.* from ( select s.id, s.created_by, s.created_date, s.last_modified_by, s.last_modified_date from hr_staff s left join sys_department d on s.dep_id = d.id
and (s.staff_code like concat ('%', #{keyword}, '%') or s.staff_name like concat ('%', #{keyword}, '%'))
and s.dep_id = #{depId}
) t, (SELECT @rownum:=0) r
