mapper.select:
select id, created_by, created_date, last_modified_by, last_modified_date from hr_staff
and (staff_code like concat ('%', #{keyword}, '%') or staff_name like concat ('%', #{keyword}, '%'))
and dep_id = #{depId}

select @rownum:=@rownum+1 as rowNum, t.* from ( select s.id, s.created_by, s.created_date, s.last_modified_by, s.last_modified_date from hr_staff s left join sys_department d on s.dep_id = d.id
and (s.staff_code like concat ('%', #{keyword}, '%') or s.staff_name like concat ('%', #{keyword}, '%'))
and s.dep_id = #{depId}
) t, (SELECT @rownum:=0) r
