mapper.select:
select id, staff_code, staff_name, personal_photo, dep_id, company_state, duty, post, post_type, post_level, sex, nation, birthday, work_date, entry_date, politics, education, degree, marital_status, spouse_name, marriage_certificate, marriage_date, children_number, id_number, phone, social_security_number, birth_address_province, birth_address_city, native_address_province, native_address_city, household_type, registered_address_province, registered_address_city, registered_address_area, registered_address_detail, home_address_province, home_address_city, home_address_area, home_address_detail, current_address_province, current_address_city, current_address_area, current_address_detail, postal_address_province, postal_address_city, postal_address_area, postal_address_detail, emergency_contact_name, emergency_contact_relation, emergency_contact_phone, created_by, created_date, last_modified_by, last_modified_date from hr_staff
and (staff_code like concat ('%', #{keyword}, '%') or staff_name like concat ('%', #{keyword}, '%'))
and dep_id = #{depId}

select @rownum:=@rownum+1 as rowNum, t.* from ( select s.id, s.staff_code, s.staff_name, d.name as depName, s.company_state, s.duty, s.post, s.post_type, s.post_level, s.sex, s.nation, s.birthday, s.work_date, s.entry_date, s.politics, s.education, s.degree, s.marital_status, s.spouse_name, s.marriage_certificate, s.marriage_date, s.children_number, s.id_number, s.phone, s.social_security_number, concat(birth_address_province, s.birth_address_city) as birth_address, concat(native_address_province, s.native_address_city) as native_address, s.household_type, concat(s.registered_address_province, s.registered_address_city, s.registered_address_area, s.registered_address_detail) as registered_address, concat(s.home_address_province, s.home_address_city, s.home_address_area, s.home_address_detail) as home_address, concat(s.current_address_province, s.current_address_city, s.current_address_area, s.current_address_detail) as current_address, concat(s.postal_address_province, s.postal_address_city, s.postal_address_area, s.postal_address_detail) as postal_address, s.emergency_contact_name, s.emergency_contact_relation, s.emergency_contact_phone, s.created_by, s.created_date, s.last_modified_by, s.last_modified_date from hr_staff s left join sys_department d on s.dep_id = d.id
and (s.staff_code like concat ('%', #{keyword}, '%') or s.staff_name like concat ('%', #{keyword}, '%'))
and s.dep_id = #{depId}
) t, (SELECT @rownum:=0) r
